$(function () {
    $('.dynamic-back').click(function() {
        parent.history.back();
        return false;
    });
	var $container = $('#portfolio-grid');
	$container.isotope({
		itemSelector: '#portfolio-grid li',
		resizable: true,
	});
	$container.imagesLoaded(function() {
		$container.isotope('layout');
	});
	var $grid = $('.image-grid');
	$grid.isotope();
	$grid.imagesLoaded( function() {
		$grid.isotope('layout');
	});
	$('#filters, #art-filters').isotope({
		layoutMode: 'fitRows'
	});
	$('#filters a').click(function() {
		var selector = $(this).attr('data-filter');
		$container.isotope({
			filter: selector
		});
		return false
	});
	$('#art-filters a').click(function() {
		var selector = $(this).attr('data-filter');
		$grid.isotope({
			filter: selector
		});
		return false
	});
});

$(function(){
	$('.carousel').each(function() {
		var $this = $(this);
		if ($this.find('div').length > 1) {
			$('.touch .swipe').fadeIn();
		}
	});
	$('.carousel').slick({
		onAfterChange: function() {
			$('.touch .swipe').fadeOut();
		}
	});
	$('.carousel-mini').slick({
		slidesToShow: 5,
		centerMode: true,
		variableWidth: true,
		focusOnSelect: true
	});
	$('.selector').on('click', function(e) {
		e.stopPropagation();
		$('.menu, .selector, .pop').toggleClass("active");
	    $(document).on('click', function(){
	        $('.menu, .selector, .pop').removeClass("active");
	    });
		$(document).scroll(function() {
			$('.menu, .selector, .pop').removeClass("active");
		});
		e.preventDefault();
	});
	$(".pop a").focus(function (e) {
		$('.menu, .selector, .pop').addClass("active");
		e.preventDefault();
	});
	$(".pop a").blur(function (e) {
		$('.menu, .selector, .pop').removeClass("active");
		e.preventDefault();
	});
});